import React, { useState, useEffect } from "react";
import { Doughnut } from "react-chartjs-2";
import axios from "axios";
import "./App.css";


let backgroundColors = [
  "rgba(54, 162, 235, 0.8)",
  "rgba(255, 206, 86, 0.8)",
  "rgba(255, 99, 132, 0.8)",
  "rgba(75, 192, 192, 0.8)",
  "rgba(153, 102, 255, 0.8)",
  "rgba(255, 159, 64, 0.8)",
  "rgba(199, 199, 199, 0.8)",
  "rgba(83, 102, 255, 0.8)",
  "rgba(40, 159, 64, 0.8)",
  "rgba(210, 199, 199, 0.8)",
  "rgba(190, 150, 120, 0.8)",
];

let borderColors = [
  "rgba(54, 162, 235, 1)",
  "rgba(255, 206, 86, 1)",
  "rgba(255, 99, 132, 1)",
  "rgba(75, 192, 192, 1)",
  "rgba(153, 102, 255, 1)",
  "rgba(255, 159, 64, 1)",
  "rgba(159, 159, 159, 1)",
  "rgba(83, 102, 255, 1)",
  "rgba(40, 159, 64, 1)",
  "rgba(210, 199, 199, 1)",
  "rgba(190, 150, 120, 1)",
];

const Houses = () => {
  const [chartData, setChartData] = useState({});

  const chart = () => {
    let houses = [];
    let chart_houses = [];
    let famNames = [];
    let count = [];
    const url = "https://thronesapi.com/api/v2/Characters";

    // get data from API using axios
    axios.get(url).then((response) => {
      let getJson = response.data;

      // convert data to json & push each data element into houses array
      getJson.forEach((element) => {
        let charFam = element.family;
        let object = houses.find((x) => x.name === charFam);
        if (object) {
          object.count += 1;
        } else {
          let family = {
            name: charFam,
            count: 1,
          };
          houses.push(family);
        }
      });

      // filter the houses array to only include count > 2
      houses.forEach((element) => {
        if (element.count >= 2) {
          let family = {
            name: element.name,
            count: element.count,
          };
          chart_houses.push(family);
        }
      });

      // separate house name & house count into individual arrays
      chart_houses.forEach((element) => {
        famNames.push(element.name);
        count.push(element.count);
      });

      // fill chart data
      setChartData({
        labels: famNames,
        datasets: [
          {
            data: count,
            backgroundColor: backgroundColors,
            borderColor: borderColors,
            hoverOffset: 15
          },
        ],
      });
    });
  };

  useEffect(() => {
    chart();
  }, []);

  /*
  return (
    <div className="chart-container">
      <h1 class="got-font">Game of Thrones</h1>
      <h4></h4>
      <Doughnut
        className="mychart"
        data={chartData}
        options={{
          responsive: true,
          legend: "bottom",
          title: { text: "GAME OF THRONES HOUSES", display: true },
        }}
      />
    </div>
  );
  */
};

export default Houses;
