import React from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";

class Search extends React.Component {
  state = {
    searchValue: "",
    character: [],
  };

  handleOnChange = (event) => {
    this.setState({ searchValue: event.target.value });
  };

  handleSearch = () => {
    this.callApi(this.state.searchValue);
  };

  callApi = (searchInput) => {
    axios.get("https://thronesapi.com/api/v2/Characters").then((repos) => {
      const jsonData = repos.data;
      jsonData.forEach((element) => {
        if (searchInput.toLowerCase() === element.firstName.toLowerCase()) {
          let ind = element.id;
          //console.log(jsonData[ind]);
          this.setState({ character: jsonData[ind] });
        }
      });
    });
  };

  render() {
    return (
      <div className="container">
        <h1>
          <span className="got-font">Game of Thrones</span> <br></br>
          Character Search
        </h1>
        <p>
          Enter the first name of the character you would like to see the image
          of:{" "}
        </p>
        <label className="search-label" htmlFor="search-input">
          <input
            name="text"
            type="text"
            placeholder="e.g. pycelle"
            onChange={(event) => this.handleOnChange(event)}
            value={this.state.searchValue}
          />
          <i className="fa fa-search search-icon" aria-hidden="true" />
        </label>
        <button
          className="search-button btn btn-dark"
          onClick={this.handleSearch}
        >
          Search
        </button>
        {this.state.character ? (
          <div id="character-container">
            <div class="single-character">
              <h2>{this.state.character.fullName}</h2>
              <img
                src={this.state.character.imageUrl}
                alt="got-character"
                width="300px"
                height="300px"
              />
            </div>
          </div>
        ) : (
          <p>Try again</p>
        )}
      </div>
    );
  }
}

export default Search;
